def ObjShapeColor(c_Index):
	SelObj=cmds.ls(sl=1,typ=["transform","joint"])
	if len(SelObj)==0:
		if c_Index=="0":
			cmds.canvas("defaultColorCanvas",e=1,hsv=(0,0,.5),ann="3")
		else:
			CV=cmds.colorIndex(int(c_Index),q=1,hsv=1)
			cmds.canvas("defaultColorCanvas",e=1,hsv=(CV[0],CV[1],CV[2]),ann=c_Index)		
		return
	for mz_everyObj in SelObj:
		mz_objShape=cmds.listRelatives(mz_everyObj,children=True,s=1 )
		for mz_everyShape in mz_objShape:
#			mz_nodeIntermediate=cmds.getAttr(mz_everyShape,".intermediateObject")
#			if mz_nodeIntermediate==0:
			cmds.setAttr(mz_everyShape+".overrideEnabled",1)
			cmds.setAttr(mz_everyShape+".overrideColor",int(c_Index))
	cmds.select(SelObj)

def CreateCtrlCur(cc_Index):	
    ToolButtonCheck=["TT1_SnapCheckB","TT1_SDKCheckB","TT1_ControlRadioBG","repaceShape_checkB"]
    ToolTab_Snap_V=cmds.checkBox(ToolButtonCheck[0],q=True,v=1)
    ToolTab_SDK_V=cmds.checkBox(ToolButtonCheck[1],q=True,v=1)
    ToolTab_Control_V=cmds.radioButtonGrp(ToolButtonCheck[2],q=True,sl=1)
    ToolTab_repace_V=cmds.checkBox(ToolButtonCheck[3],q=True,v=1)
    colocIndex_V=cmds.canvas("defaultColorCanvas",q=1,ann=1)
    selectObj=cmds.ls(sl=1)
    cctrl=cctrlGp=cctrlSdk=""
    dates=MZ_CtrlCurClass
    if len(selectObj)==0 or ToolTab_Snap_V==0 :
        if cc_Index=="29":
            QS=cmds.circle(c=[0,0,0],nr=[0,1,0],sw=360,r=1,d=3,ut=0,tol=0.0930737,s=8)
            cctrl=QS[0]
        else:
            Zi=int(cc_Index)-1
            cctrl=cmds.curve(d=1,p=dates.ct[Zi][0],k=dates.ct[Zi][1])		
        cctrl=cmds.rename(cctrl,MZ_nameIteration("__Ctrl"))
        ObjShapeColor(str(colocIndex_V))
        if ToolTab_SDK_V==0:
            cctrlGp=cmds.group(cctrl,n=cctrl+"_grp")
            cmds.xform(cctrlGp,os=1,piv=[0,0,0])
        else:
            cctrlSdk=cmds.group(cctrl,n=cctrl+"_SDK")
            cmds.xform(cctrlSdk,os=1,piv=[0,0,0])
            cctrlGp=cmds.group(cctrlSdk,n=cctrl+"_grp")
            cmds.xform(cctrlGp,os=1,piv=[0,0,0])
        cmds.select(cl=1)
    elif ToolTab_repace_V==0:
        ctrllist = []
        for everyObj in selectObj:
            mz_nodeType=cmds.nodeType(everyObj)
            if mz_nodeType=="transform" or mz_nodeType=="joint":
                if cc_Index=="29":
                    QS=cmds.circle(c=[0,0,0],nr=[0,1,0],sw=360,r=1,d=3,ut=0,tol=0.0930737,s=8)
                    cctrl=QS[0]
                else:
                    Zi=int(cc_Index)-1
                    cctrl=cmds.curve(d=1,p=dates.ct[Zi][0],k=dates.ct[Zi][1])
                cctrl=cmds.rename(cctrl,everyObj+"_Ctrl")
                ObjShapeColor(str(colocIndex_V))
                if ToolTab_SDK_V==0:
                    cctrlGp=cmds.group(cctrl,n=cctrl+"_grp")
                    cmds.xform(cctrlGp,os=1,piv=[0,0,0])
                else:
                    cctrlSdk=cmds.group(cctrl,n=cctrl+"_SDK")
                    cmds.xform(cctrlSdk,os=1,piv=[0,0,0])
                    cctrlGp=cmds.group(cctrlSdk,n=cctrl+"_grp")
                    cmds.xform(cctrlGp,os=1,piv=[0,0,0])
                cmds.delete(cmds.parentConstraint(everyObj,cctrlGp,weight=1))
                if ToolTab_Control_V==2:
                    cmds.parentConstraint(cctrl,everyObj,weight=1)
                if ToolTab_Control_V==3:
                    cmds.parent(everyObj,cctrl)
                if ToolTab_Control_V==4:
                    Tv=cmds.getAttr((everyObj+".translate"))
                    print (Tv[0])
                    Rv=cmds.getAttr((everyObj+".rotate"))
                    Sv=cmds.getAttr((everyObj+".scale"))			
                    if Tv[0]==(0,0,0) and Rv[0]==(0,0,0)and Sv[0]==(1.0,1.0,1.0):
                        cmds.connectAttr((cctrl+".translateX"),(everyObj+".translateX"),f=1)
                        cmds.connectAttr((cctrl+".translateY"),(everyObj+".translateY"),f=1)
                        cmds.connectAttr((cctrl+".translateZ"),(everyObj+".translateZ"),f=1)
                        cmds.connectAttr((cctrl+".rx"),(everyObj+".rx"),f=1)
                        cmds.connectAttr((cctrl+".ry"),(everyObj+".ry"),f=1)
                        cmds.connectAttr((cctrl+".rz"),(everyObj+".rz"),f=1)
                        cmds.connectAttr((cctrl+".sx"),(everyObj+".sx"),f=1)
                        cmds.connectAttr((cctrl+".sy"),(everyObj+".sy"),f=1)
                        cmds.connectAttr((cctrl+".sz"),(everyObj+".sz"),f=1)
                    else:
                        cmds.select(selectObj)
                        cmds.error("你要做属性连接控制，被控制的物体的位移旋转属性归零")
            if cmds.checkBox('TT1_ParentCheckB', q=1, v=1):
                    ctrllist.append(cctrl)
        for i in range(len(ctrllist)-1, 0, -1):
            cmds.parent(ctrllist[i] + '_grp', ctrllist[i-1])
        cmds.select(selectObj)
    else:
        for everyObj in selectObj:
            mz_objShape=cmds.listRelatives(everyObj,children=True,s=1 )
            for mz_everyShape in mz_objShape:
                if cc_Index=="29":
                    QS=cmds.circle(c=[0,0,0],nr=[0,1,0],sw=360,r=1,d=3,ut=0,tol=0.0930737,s=8)
                    cctrl=QS[0]
                else:
                    Zi=int(cc_Index)-1
                    cctrl=cmds.curve(d=1,p=dates.ct[Zi][0],k=dates.ct[Zi][1])
                cmds.rename(cctrl,"XXXXX_Ctrl")
                ccolorI=cmds.getAttr(mz_everyShape+".overrideColor")
                cmds.setAttr("XXXXX_CtrlShape.overrideEnabled",1)
                cmds.setAttr("XXXXX_CtrlShape.overrideColor",ccolorI)
                cmds.parent("XXXXX_CtrlShape",everyObj,r=1,s=1)
                cmds.delete(mz_everyShape,"XXXXX_Ctrl")
                cmds.rename("XXXXX_CtrlShape",mz_everyShape)
        cmds.select(selectObj) 

def MZ_parentCurveShapeNode():
	sel=cmds.ls(sl=1,type=["transform"])
	if len(sel)==0 and  len(sel)==1:
		cmds.error=("物体选择的不正确，请选着目标曲线物体，在加选位置物体")
	i=0
	while i<len(sel)-1:
		ShapeNode=cmds.listRelatives(sel[i],children=True,s=1,type="nurbsCurve")
		for everyShape in ShapeNode:
			cmds.parent(everyShape,sel[len(sel)-1],r=1,s=1)
		cmds.delete(sel[i])
		i=i+1
	allShapeNode=cmds.listRelatives(sel[len(sel)-1],children=True,s=1,type="nurbsCurve")
	j=1
	while j<len(allShapeNode):
		cmds.rename(allShapeNode[j],sel[len(sel)-1]+str(j)+"Shape")
		j=j+1
	cmds.select(sel[len(sel)-1]) 

